import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react'

class Clock extends React.Component{
constructor(props){
super(props);
this.state={
    clock: 25,
    break: 5,
 }
 
}

render(){
    return (
        <>
         <h2>25+5 I love C o C</h2>
         <div className='d-flex justify-content-center'>
            <div>
             <h2 id="break-label">Break Length</h2>
             <button id="break-decrement">down</button>
             <h3 id="break-length">{this.state.break}</h3>
             <button id="session-decrement">up</button>
            </div>

            <div>
             <h2 id="session-increment">Session Length</h2>
             <button id="break-increment">down</button>
             <h3 id="session-length">{this.state.clock}</h3>
             <button id="session-increment">up</button>
            </div>
          </div>
        </>
    )
  }
}

export default Clock
