import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react'
import { connect } from 'react-redux';
import { useState, useEffect} from 'react';

const ClockOp=({idLab, idInc, idDec, idLen, nameLength, valor, boton})=>{
    return( 
        <>
    <h2 id={idLab}>{nameLength}</h2>
    <button onClick={boton(idDec)} id={idDec}>down</button>
    <h3 id={idLen}>{valor}</h3>
    <button onClick={boton(idInc)} id={idInc}>up</button>
        </>
 )
}




class Principal extends React.Component{
    constructor(props){
        super(props);
        this.state={
          r:0,
            session: 25,
            break: 5,
          clock: 25,
          //si este llega a 0 restarle 1 a clock 
          seconds: 0,
          second: 100,
          
          //si este llega a 0 restarle 1 a seconds
          onOff: false,
          
         }
         this.breakControl = this.breakControl.bind(this);
         this.clockLogic = this.clockLogic.bind(this);
        }

    
   

   
        render(){
            return (
                <>
                 <h2>25+5 I love C o C</h2>
                 <div className='d-flex justify-content-center'>
                    <div>
                     <ClockOp 
                     idLab="break-label"
                     nameLength="Break Length"
                     idInc="break-increment"
                     idDec="break-decrement"
                     idLen="break-length"
                     valor={this.state.break}
                     boton={this.breakControl}
                     />
                    </div>
        
                    <div>
                     <ClockOp 
                     idLab="session-label"
                     nameLength="Session Length"
                     idInc="session-increment"
                     idDec="session-decrement"
                     idLen="session-length"
                     valor={this.state.session}
                     boton={this.breakControl}
                     />
                    </div>
                  </div>
                   <div>
                     <h3 id="timer-label">Session</h3>
                     <h1 id="time-left">{this.state.second}</h1>
                     <button id="play" onClick={this.clockLogic("play")}>Play</button>
                     <button id="pause"onClick={this.clockLogic("pause")}>Pause</button>
                     <button id="reset" >Reset</button>
                     <h2 id="interval">{}</h2>
                   </div>
                </>
            )
          }
}

export default connect(mapStateToProps,{Play})(Principal)
