const resta = "resta"

const restar=()=>{
  return{
    type: resta
  }
}

const estado = {
    second: 0,
   }
   
   const clockController =(state=estado, action)=>{
     switch(action.type){
       case "resta": return{
         second: state.second-1
       }
       default: return state
     }
   }

   export default clockController
   export {restar}
