import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react'
import { useState, useEffect} from 'react';

const ClockOp=({idLab, idInc, idDec, idLen, nameLength, valor, boton})=>{
    return( 
        <>
    <h2 id={idLab}>{nameLength}</h2>
    <button onClick={boton} id={idDec}>down</button>
    <h3 id={idLen}>{valor}</h3>
    <button onClick={boton} id={idInc}>up</button>
        </>
 )
}




class Principal extends React.Component{
    constructor(props){
        super(props);
        const [session, putSession] = useState(25);
        const [breakk, putBreakk] = useState(5);
        const [sessionDur, setSessionDur] = useState(session*60);
        const [breakDur, setBreakDur] = useState(session*60);
        }

        render(){
          
            return (
                <>
                 <h2>25+5 I love C o C</h2>
                 <div className='d-flex justify-content-center'>
                    <div>
                     <ClockOp 
                     idLab="break-label"
                     nameLength="Break Length"
                     idInc="break-increment"
                     idDec="break-decrement"
                     idLen="break-length"
                     valor=""
                     boton=""
                     />
                    </div>
        
                    <div>
                     <ClockOp 
                     idLab="session-label"
                     nameLength="Session Length"
                     idInc="session-increment"
                     idDec="session-decrement"
                     idLen="session-length"
                     valor=""
                     boton=""
                     />
                    </div>
                  </div>
                   <div>
                     <h3 id="timer-label">Session</h3>
                     <h1 id="time-left">{}</h1>
                     <button id="play" >Play</button>
                     <button id="pause">Pause</button>
                     <button id="reset" >Reset</button>
                     <h2 id="interval">{}</h2>
                   </div>
                </>
            )
          }
}

export default Principal
