const play="play"



const estado={
  second: 20,
};

const clockReducer =(state=estado, action)=>{
   switch(action.type){
    case play:
        return{
          second: state.second-1
      }
      default: return state
   }
}

const Play=()=>{
    return{
        type: "play"
    }
  }

export default clockReducer
export {play}
