import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react'


const ClockCalc=({idLab, idInc, idDec, idLen, nameLength, valor})=>{
    return( 
        <>
    <h2 id={idLab}>{nameLength}</h2>
    <button id={idDec}>down</button>
    <h3 id={idLen}>{valor}</h3>
    <button id={idInc}>up</button>
        </>
 )
}


class Principal extends React.Component{
    constructor(props){
        super(props);
        this.state={
            session: 25,
            break: 5,
          clock: 25,
          //si este llega a 0 restarle 1 a clock 
          seconds: 0,
          second: 0
          //si este llega a 0 restarle 1 a seconds
         }
         
        }
        
        render(){
            return (
                <>
                 <h2>25+5 I love C o C</h2>
                 <div className='d-flex justify-content-center'>
                    <div>
                     <ClockCalc 
                     idLab="break-label"
                     nameLength="Break Length"
                     idInc="break-increment"
                     />
                    </div>
        
                    <div>
                     <h2 id="session-label">Session Length</h2>
                     <button id="break-increment">down</button>
                     <h3 id="session-length">{this.state.session}</h3>
                     <button id="session-increment">up</button>
                    </div>
                  </div>
                   <div>
                     <h3 id="timer-label">Session</h3>
                     <h1 id="time-left">{this.state.clock}:{this.state.seconds}{this.state.second}</h1>
                   </div>
                </>
            )
          }
}

export default Principal
